mapper.select:

select id, name, password
from user
where id = #{id}


select no, name, email, gender 
from user 
where email = #{email }

choose.when:

update user 
set name = #{name}, gender = #{gender} 
where no = #{no}

choose.otherwise:

update user 
set name = #{name}, password = password(#{password}) 
where no = #{no}
